import React, { Component } from "react";
import { Navbar, Nav } from "react-bootstrap";
import { Link } from "react-scroll";

class Header extends Component {
  state = {
    navbarclass: "topnav",
    navbarvariant: "light"
  };

  listenScrollEvent = e => {
    if (window.scrollY > 50) {
      this.setState({ navbarclass: "scrollingnav" });
      this.setState({ navbarvariant: "dark" });
    } else {
      this.setState({ navbarclass: "topnav" });
      this.setState({ navbarvariant: "light" });
    }
  };

  componentDidMount() {
    window.addEventListener("scroll", this.listenScrollEvent);
  }

  render() {
    return (
      <Navbar
        variant={this.state.navbarvariant}
        className={`text-center ${this.state.navbarclass}`}
        expand="lg"
        fixed="top"
      >
        <Navbar.Brand href="/">Randy Meneses</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbar-nav" />
        <Navbar.Collapse id="navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link>
              <Link
                activeClass="active"
                to="aboutMe"
                spy={true}
                smooth={true}
                duration={500}
              >
                About Me
              </Link>
            </Nav.Link>
            <Nav.Link>
              {" "}
              <Link
                activeClass="active"
                to="projects"
                spy={true}
                smooth={true}
                duration={500}
              >
                Projects
              </Link>
            </Nav.Link>
          </Nav>
          <Nav>
            <Nav.Link>
              {" "}
              <Link
                activeClass="active"
                to="contactMe"
                spy={true}
                smooth={true}
                duration={500}
              >
                Contact Me
              </Link>
            </Nav.Link>
            <p>
              <a href="file://../../resume.pdf" download target="blank">
                test pdf
              </a>
            </p>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    );
  }
}

export default Header;
